#pragma once
#ifndef BEACON_ENV_TYPE_PARAMETER_H
#define BEACON_ENV_TYPE_PARAMETER_H
#include "../util/string_pool.h"
#include "../util/vector.h"
#include <stdbool.h>
struct ILTypeParameter;
/**
 * 型変数の種類を表す列挙.
 */
typedef enum bc_TypeParameterKind {
	TYPE_PARAMETER_KIND_DEFAULT_T,
	TYPE_PARAMETER_KIND_IN_T,
	TYPE_PARAMETER_KIND_OUT_T,
} bc_TypeParameterKind;

/**
 * 型変数を表す構造体.
 */
typedef struct bc_TypeParameter {
	bc_StringView Name;
	bc_TypeParameterKind Kind;
} bc_TypeParameter;
/**
 * 新しい型変数を生成します.
 * @param name
 * @return
 */
bc_TypeParameter* bc_NewTypeParameter(bc_StringView name);

/**
 * ILから型変数を生成します.
 * @param src
 * @param return
 */
bc_TypeParameter* bc_DupTypeParameter(struct ILTypeParameter* src);

/**
 * ILから型変数を生成します.
 * @param ilSource
 * @param sgDest
 */
void bc_DupTypeParameterList(bc_Vector* ilSource, bc_Vector* sgDest);

/**
 * 型変数の一覧を出力します.
 * @param v
 */
void bc_PrintTypeParameter(bc_Vector* v);

/**
 * 型変数を解放します.
 * @param self
 */
void bc_DeleteTypeParameter(bc_TypeParameter* self);

/**
 * 重複するパラメータ名が存在するなら true.
 * @param tparameters
 * @param namev
 * @return
 */
bool bc_IsOverwrappedTypeParameterName(bc_Vector* tparameters, bc_StringView* namev);
#endif // !SIGNAL_ENV_TYPE_PARAMETER_H
