#ifndef BEACON_IL_IL_PROPERTY_BODY_H
#define BEACON_IL_IL_PROPERTY_BODY_H
#include <stdbool.h>
#include "../ast/access_level.h"
#include "../util/vector.h"

struct bc_GenericCache;

typedef enum bc_ILPropertyBodyTag {
        IL_PROPERTY_SET_T,
        IL_PROPERTY_GET_T,
} bc_ILPropertyBodyTag;

typedef struct bc_ILPropertyBody {
        bc_AccessLevel Access;
        bc_Vector* Statements;
        bc_ILPropertyBodyTag Tag;
        bool IsShort;
} bc_ILPropertyBody;

#define bc_NewILPropertyBody(tag) \
        (bc_MallocILPropertyBody(tag, __FILE__, __LINE__))
bc_ILPropertyBody* bc_MallocILPropertyBody(bc_ILPropertyBodyTag tag,
                                           const char* filename, int lineno);

void bc_DeleteILPropertyBody(bc_ILPropertyBody* self);
#endif