#pragma once
#ifndef BEACON_IL_IL_STMT_INTERFACE_H
#define BEACON_IL_IL_STMT_INTERFACE_H
#include "../vm/enviroment.h"
#include "../error.h"
#include "call_context.h"
#include <stdint.h>
/**
 * 文の種類を表す列挙型.
 */
typedef enum bc_ILStatementTag {
	ILSTMT_PROC_T,
	ILSTMT_IF_T,
	ILSTMT_WHILE_T,
	ILSTMT_VARIABLE_DECL_T,
	ILSTMT_VARIABLE_INIT_T,
	ILSTMT_RETURN_T,
	ILSTMT_RETURN_EMPTY_T,
	ILSTMT_BREAK_T,
	ILSTMT_CONTINUE_T,
	ILSTMT_INFERENCED_TYPE_INIT_T,
	ILSTMT_TRY_T,
	ILSTMT_THROW_T,
	ILSTMT_ASSERT_T,
	ILSTMT_DEFER_T,
	ILSTMT_YIELD_RETURN_T,
	ILSTMT_YIELD_BREAK_T,
	ILSTMT_INJECT_JNI_T,
} bc_ILStatementTag;

//詳細は il_stmt_impl.h を参照してください。
struct ILProc;
struct ILIf;
struct ILWhile;
struct ILVariableDecl;
struct ILVariableInit;
struct ILReturn;
typedef void* ILReturnEmpty;
struct ILInferencedTypeInit;
struct ILTry;
struct ILThrow;
struct ILAssert;
struct ILDefer;
struct ILYieldReturn;
typedef void* ILStatementYieldBreak;
struct ILInjectJNI;

/**
 * 文を表す要素です.
 */
typedef struct bc_ILStatement {
	bc_ILStatementTag Type;
	int Lineno;
	union {
		struct ILProc* Proc;
		struct ILIf* If;
		struct ILWhile* While;
		struct ILVariableDecl* VariableDecl;
		struct ILVariableInit* VariableInit;
		struct ILReturn* Return;
		ILReturnEmpty ReturnEmpty;
		struct ILInferencedTypeInit* InferencedTypeInit;
		struct ILTry* Try;
		struct ILThrow* Throw;
		struct ILAssert* Assert;
		struct ILDefer* Defer;
		struct ILYieldReturn* YieldReturn;
		ILStatementYieldBreak YieldBreak;
		struct ILInjectJNI* InjectJNI;
	} Kind;
} bc_ILStatement;

#define bc_NewILStatement(type) (bc_MallocILStmt(type, __FILE__, __LINE__))
bc_ILStatement* bc_MallocILStmt(bc_ILStatementTag type, const char* filename, int lineno);

/**
 * 文を生成します.
 * @param self
 * @param env
 * @param cctx
 */
void bc_GenerateILStmt(bc_ILStatement* self, bc_Enviroment* env, bc_CallContext* cctx);

/**
 * ステートメントを読み込んでエラーがあれば記録します.
 * @param self
 * @param env
 * @param cctx
 */
void bc_LoadILStmt(bc_ILStatement * self, bc_Enviroment* env, bc_CallContext* cctx);

/**
 * 文を開放します.
 * @param self
 */
void bc_DeleteILStmt(bc_ILStatement* self);
#endif // !SIGNAL_IL_IL_STMT_INTERFACE_H
